import { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(){
    super();

    this.state = {
      monsters: [],
      searchField: ''
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then((response) => response.json())
    .then((users) =>  this.setState(() => {
      return {monsters : users}
    }))
  }

  render() {
    const filteredMonsters = this.state.monsters.filter((monster) => monster.name.toLocaleLowerCase().includes(this.state.searchField));  

    return (
      <div className="App">
        <input className='searchBox' type='search' placeholder='Search Monsters' onChange={(event) => {
          const searchField = event.target.value.toLocaleLowerCase()
          this.setState(() => {
            return { searchField };
          })

        }}/>
        {
          this.state.monsters.map((monster) => {
            return <div key={ monster.id }>
                <h1>{monster.name}</h1>
              </div>
          })
        }
      </div>
    );
  }
}

export default App;
